import React from "react";
import Cookies from "js-cookie";

function SignUp() {

    const [username, setUser] = React.useState('');
    const [password, setPassword] = React.useState('');
    const [repassword, setRePassword] = React.useState('');
    const [result, setResult] = React.useState(null);

    setCookie(username);
    console.log(getCookie);
    const myHandler = () => {

        var errorMessage = document.getElementById("ErrMessage");

        if(username == "")
        {
            errorMessage.innerHTML = "Username is not valid. Enter valid username.";
            errorMessage.style.backgroundColor = "red";
            return false;
        }
        else if(password == "")
        {
            errorMessage.innerHTML = "Password is not valid. Enter validpassword";
            errorMessage.style.backgroundColor = "red";
            return false;
        }
        else if(repassword == "")
        {
            errorMessage.innerHTML = "Please re-enter password";
            errorMessage.style.backgroundColor = "red";
            return false;
        }
        else
        {
            errorMessage.innerHTML = "";
        }

        var ABC = new RegExp(
            "(?=.*[A-Z])"
        );
        var one = new RegExp(
            "(?=.*\\d)"
        );

        if (ABC.test(username)) {
        } else {
            errorMessage.innerHTML = "Username requires atleast 1 capital letter";
            errorMessage.style.backgroundColor = "red";
            return false;
        }
        if (username.length <= 6) {
            errorMessage.innerHTML = "Username must be more than 6 characters";
            errorMessage.style.backgroundColor = "red";
            return false;

        } if (ABC.test(password)) {
        } else {
            errorMessage.innerHTML = "Password requires atleast 1 capital letter";
            errorMessage.style.backgroundColor = "red";
            return false;


        } if (password.length <= 6) {
            errorMessage.innerHTML = "Password must be more than 6 characters";
            errorMessage.style.backgroundColor = "red";
            return false;
        } if (one.test(password)) {
        } else {
            errorMessage.innerHTML = "Password requires a numerical value";
            errorMessage.style.backgroundColor = "red";
            return false;
        }
        if (password != repassword) {
            errorMessage.innerHTML = "Passwords do not match";
            errorMessage.style.backgroundColor = "red";
            return false;

        } else {
            console.log('Username= ' + username);
            console.log('Password= ' + password);
            console.log('RePassword= ' + repassword)
        }
        Cookies.set("loggedIn", username, { expires: 10 });  
        console.log(Cookies.get("loggedIn"));


        const body = {
           
            username: username,
            password: password,
            repassword: repassword
        };
        const settings = {
            method: 'post',
            body: JSON.stringify(body),
        };
        fetch('sign-up', settings)
            .then(res => res.json())
            .then(data => {
                console.log(data);
                if (data.isSuccess) {
                    setResult(true);
                    setCookie(username);
                    window.location.replace('../');
                } else {
                    setResult(data.error);
                    errorMessage.style.backgroundColor = "red";
                }
            })
            .catch(console.log);
    };

    function setCookie(username) {
        const d = new Date();
        d.setTime(d.getTime() + (10 * 24 * 60 * 60 * 1000));
        let expires = "expires" + d.toUTCString();
        document.cookie = username + "=" + username + ";" + ";path=/";
    }
    function getCookie(username) {
        let name = username + "=";
        let c1 = document.cookie.split(';');
        for (let i = 0; i < c1.length; i++) {
            let c = c1[i];
            while (c.charAt(0) == ' ') {
                c = c.substring(1);
            }
            if (c.indexOf(name) == 0) {
                return c.substring(name.length, c.length);
            }
        }
        return "";
    }

    function checkCookie() {
        let username = getCookie(username);
        if (username != "") {
            console.log("session active")
            return true;
        } else {
            console.log("session over")
            return false;
        }
    }
    return (

        <div className="sign-up-area field">
            <div className="contents">
                <h2 className="signUpHeader">Sign Up for Yasuo Pay</h2>
                <div className="setUsername">
                    <label>Enter Username</label>
                    <br />
                    <input id="username" value={username} onChange={(e) => 
                        setUser(e.target.value)} className="input">
                    </input>
                    <br />

                    <label>Enter Password</label>
                    <br />
                    <input type="password" value={password} onChange={(e) => 
                        setPassword(e.target.value)} className="input">
                    </input>
                    <br />

                    <label>Confirm Password</label>
                    <br />
                    <input type="password" value={repassword} onChange={(e) => 
                        setRePassword(e.target.value)} className="input">
                    </input>
                    <br />
                    <br />

                    
                    <div className = "error" id = "ErrMessage"></div>

                    <button onClick={myHandler} >Submit</button>
                </div>
            </div>
        </div>

    );
}

export default SignUp;
