  //       console.log("Email or Password is not matching with our record");
  //     }
  //   } else {
  //     console.log("Email or Password is not matching with our record");
  //   }
  // };
  return (
    <>
    <h1 className="login">Login To FOODIE</h1>
      <p className="loginForm">Login Form</p>

      <form className="appForm" onSubmit={handleSubmit(onSubmit)}>
      <p>Email</p>
        <input type="email" value={email}{...register("email", { required: true })} />
       
        {errors.email && <span style={{ color: "red" }}>
         *Email* is mandatory </span>}
         <p>Password</p>
        <input type="password" value={password}{...register("password")} />
        <button onClick={handleSubmit} className="btnSubmit" type="submit">
          Submit
        </button>
      </form>
    </>
  );
}
export default Login;
